import{_ as l,c as a,o as t,a3 as i}from"./chunks/framework.CScuXPqu.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"daily/html.md","filePath":"daily/html.md"}'),e={name:"daily/html.md"},s=i('<h2 id="html-语义化是什么-有什么优点" tabindex="-1">HTML 语义化是什么？有什么优点 <a class="header-anchor" href="#html-语义化是什么-有什么优点" aria-label="Permalink to &quot;HTML 语义化是什么？有什么优点&quot;">​</a></h2><details class="details custom-block"><summary>查看详情</summary><ul><li>HTML 语义化就是让页面内容结构化，便于浏览器、搜索引擎解析，在没有样式的情况下也以一种文档格式显示，且是容易阅读的。</li><li>优点 <ul><li>代码易于阅读，易于维护</li><li>有利于 SEO</li></ul></li></ul></details><h2 id="html5-新标签" tabindex="-1">HTML5 新标签 <a class="header-anchor" href="#html5-新标签" aria-label="Permalink to &quot;HTML5 新标签&quot;">​</a></h2><details class="details custom-block"><summary>查看详情</summary><ul><li>header: 定义页面或章节的头部</li><li>footer: 定义页面或章节的尾部</li><li>nav: 定义只包含导航链接的章节</li><li>svg: 定义一个嵌入式矢量图</li><li>aside: 定义和页面内容关联度较低的内容</li><li>video</li><li>audio</li><li>canvas</li></ul></details><h2 id="img-标签的-alt-和-title-的区别" tabindex="-1">img 标签的 alt 和 title 的区别 <a class="header-anchor" href="#img-标签的-alt-和-title-的区别" aria-label="Permalink to &quot;img 标签的 alt 和 title 的区别&quot;">​</a></h2><details class="details custom-block"><summary>查看详情</summary><ul><li>alt：在图片不能正常显示的时候出现的文本提示，有利于 SEO</li><li>title：是在鼠标移动到元素上的文本提示</li></ul></details><h2 id="为什么-css-放在顶部而-js-放在底部" tabindex="-1">为什么 CSS 放在顶部而 JS 放在底部？ <a class="header-anchor" href="#为什么-css-放在顶部而-js-放在底部" aria-label="Permalink to &quot;为什么 CSS 放在顶部而 JS 放在底部？&quot;">​</a></h2><details class="details custom-block"><summary>查看详情</summary><ul><li>CSS 不会阻塞 DOM 解析（但会阻塞页面的渲染）</li><li>JS 会阻塞 DOM 解析（但浏览器会‘偷看’DOM，预先下载相关资源）</li></ul></details><h2 id="html代码的第一行的作用" tabindex="-1">html代码的第一行的作用 <a class="header-anchor" href="#html代码的第一行的作用" aria-label="Permalink to &quot;html代码的第一行的作用&quot;">​</a></h2><details class="details custom-block"><summary>查看详情</summary><p>HTML代码的第一行通常是文档类型声明（DOCTYPE declaration），其作用是告知浏览器使用哪种HTML或XHTML版本来解析文档。这是HTML文档的基础，确保浏览器在渲染页面时的一致性。</p></details><h2 id="html里的meta标签作用是什么" tabindex="-1">html里的meta标签作用是什么？ <a class="header-anchor" href="#html里的meta标签作用是什么" aria-label="Permalink to &quot;html里的meta标签作用是什么？&quot;">​</a></h2><details class="details custom-block"><summary>查看详情</summary><p>常见的 meta 属性及其作用</p><ul><li>charset：定义文档的字符编码，确保正确显示文本内容</li><li>name 和 content：提供文档的一些基本信息，如作者、描述、关键词等。</li><li>viewport：控制页面的布局和缩放，特别在移动设备上很重要。</li><li>http-equiv：用于模拟HTTP响应头。</li><li>robots：为搜索引擎提供爬取和索引页面的指示。</li></ul></details>',12),m=[s];function o(r,d,c,h,u,n){return t(),a("div",null,m)}const T=l(e,[["render",o]]);export{b as __pageData,T as default};
